import {
  CommentsDTO,
  DoctorsDTO,
  likeDTO,
  ReviewsDTO,
} from "../../models/doctors";
import { ResponseError } from "../../modules/error/response_error";
import {
  addComment,
  addDoctor,
  booked,
  checkDoctorPhoneNumberExists,
  deleteDoctor,
  doctorSchadules,
  doctorScheduleTimeAvailability,
  existingFeedback,
  filterServicesById,
  filterSpecialtyById,
  getDoctoLike,
  getDoctorsDetailClinic,
  getDoctorsFromElastic,
  getReviews,
  getServices,
  getSpecialties,
  getSubSpecialtiesById,
  insertReviews,
  like,
  recordDoctorProfileView,
  updateDoctor,
} from "./db";

import { fetchDoctors } from "./db";

const moment = require("moment");

export class DoctorsService {
  public static async getDoctors(filters: {
    first_name?: string;
    last_name?: string;
    id?: string;
    city_id?: string;
    doctor_id?: string;
    service_id: string;
    specialty_id: string;
    patient_id: string;
    insurance_id: string;
    minRating?: number;
    maxRating?: number;
    isPopular?: boolean;
  }) {
    const data = await getDoctorsFromElastic(filters);
    if (data) {
      return { message: "ok", data };
    } else {
      return null;
    }
  }

  public static async getClinicDoctors() {
    const data = await fetchDoctors();
    if (data) {
      return { message: "ok", data };
    }else{
      return null
    }
  }

  // public static async getClinicDoctors() {
  //   const data = await fetchDoctors();
  //   if (data) {
  //     return { message: "ok", data };
  //   } else {
  //     return null;
  //   }

  // }
  public static async getDoctorDetailClinic(id: number) {
    const data = await getDoctorsDetailClinic(id);
    if (data) {
      return { message: "ok", data };
    } else {
      return null;
    }
  }

  public static async checkDoctorPhoneNumberExists(
    mobile: string
  ): Promise<boolean> {
    const data = await checkDoctorPhoneNumberExists(mobile);
    if (data) {
      return true;
    } else {
      return false;
    }
  }
  public static async registerDoctor(formData: DoctorsDTO) {
    const data = await addDoctor(formData);
    if (data) {
      return { message: "ok", data };
    } else {
      return null;
    }
  }
  // public static async doctorDetial(doctortId: number) {
