export async function getClinicByPassword(
  email: string,
  password: string
): Promise<any> {
  try {
    const user = await query<RowDataPacket[]>(
      // ` SELECT u.*, r.*,p.*
      // LEFT JOIN ${coreSchema}.user_roles ur ON u.id = ur.id
      // LEFT JOIN ${coreSchema}.roles r ON ur.role_id = r.id
